import React, { useState } from 'react';

const SimpleInput = (props) => {
  const [name, setName] = useState('');
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [userTouched, setUserTouched] = useState(false);

  const nameIsInvalid = enteredNameIsValid && userTouched;
  console.log(nameIsInvalid);
  const inputClass = nameIsInvalid ? 'form-control invalid' : 'form-control';


  const validation = () => {
    console.log('validation');
    if (name.trim() === '') {
      setEnteredNameIsValid(false);
    } else {
      setEnteredNameIsValid(true);
    };
  };

  const submitFunction = (e) => {
    e.preventDefault();
    console.log('submitFunction');
    setEnteredNameIsValid(true)
    validation();
    console.log('submitFunctionBeforeValidation');
  };

  return (
    <form onSubmit={submitFunction}>
      <div className={inputClass}>
        <label htmlFor='name'>Your Name</label>
        <input
          type='text'
          id='name'
          value={name}
          onChange={input => setName(input.target.value)}
        />
        {nameIsInvalid && <p className='error-text'>The name cannot be empty!</p>}
      </div>
      <div className="form-actions">
        <button type='submit'>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
